import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { FiGithub, FiExternalLink, FiPlay, FiCode, FiStar } from 'react-icons/fi';
import './Projects.css';

const Projects = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const [filter, setFilter] = useState('all');

  const projects = [
    {
      id: 1,
      title: "ChatGPT Wrapper for Unity",
      description: "An open-source Unity package that integrates ChatGPT API for seamless AI conversations in Unity projects. Features custom UI components and easy-to-use scripts.",
      category: "open-source",
      technologies: ["Unity 3D", "C#", "OpenAI API", "JSON"],
      github: "https://github.com/abdullah6930/ChatGPTWrapper_Unity",
      demo: "https://just-catch-boxes.web.app/",
      image: "/api/placeholder/400/250",
      featured: true,
      status: "completed"
    },
    {
      id: 2,
      title: "Mesh Editor for Unity",
      description: "A powerful mesh generation and editing tool for Unity that allows developers to create custom geometry programmatically with an intuitive interface.",
      category: "open-source",
      technologies: ["Unity 3D", "C#", "Mesh Generation", "Editor Tools"],
      github: "https://github.com/abdullah6930/MeshGeneration",
      image: "/api/placeholder/400/250",
      featured: true,
      status: "completed"
    },
    {
      id: 3,
      title: "VR Training Simulator",
      description: "Enterprise VR application for safety training in industrial environments. Features hand tracking, realistic physics, and comprehensive analytics.",
      category: "vr-ar",
      technologies: ["Unity 3D", "Oculus SDK", "Hand Tracking", "Analytics"],
      image: "/api/placeholder/400/250",
      featured: true,
      status: "completed"
    },
    {
      id: 4,
      title: "AR Marketing Application",
      description: "Augmented reality application for retail marketing that allows customers to visualize products in their environment before purchase.",
      category: "vr-ar",
      technologies: ["Unity 3D", "AR Foundation", "Vuforia", "3D Modeling"],
      image: "/api/placeholder/400/250",
      status: "completed"
    },
    {
      id: 5,
      title: "FPS Multiplayer Game",
      description: "Fast-paced online multiplayer shooter with custom networking, player progression, and competitive gameplay mechanics.",
      category: "games",
      technologies: ["Unity 3D", "Mirror Networking", "C#", "Blender"],
      image: "/api/placeholder/400/250",
      status: "in-progress"
    },
    {
      id: 6,
      title: "Educational Science Simulator",
      description: "Interactive educational application teaching physics concepts through virtual experiments and simulations for K-12 students.",
      category: "games",
      technologies: ["Unity 3D", "Physics", "Educational Tech", "UI/UX"],
      image: "/api/placeholder/400/250",
      status: "completed"
    },
    {
      id: 7,
      title: "AI-Powered NPC System",
      description: "Advanced NPC behavior system using machine learning for dynamic conversations and adaptive gameplay responses.",
      category: "ai",
      technologies: ["Unity 3D", "ML-Agents", "Python", "TensorFlow"],
      image: "/api/placeholder/400/250",
      status: "in-progress"
    },
    {
      id: 8,
      title: "Procedural World Generator",
      description: "Tool for generating infinite, procedural game worlds with biomes, terrain features, and resource distribution.",
      category: "games",
      technologies: ["Unity 3D", "Procedural Generation", "Noise Functions", "Optimization"],
      image: "/api/placeholder/400/250",
      status: "completed"
    }
  ];

  const categories = [
    { id: 'all', label: 'All Projects' },
    { id: 'open-source', label: 'Open Source' },
    { id: 'vr-ar', label: 'VR/AR' },
    { id: 'games', label: 'Games' },
    { id: 'ai', label: 'AI/ML' }
  ];

  const filteredProjects = filter === 'all' 
    ? projects 
    : projects.filter(project => project.category === filter);

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { y: 50, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        duration: 0.6,
        ease: "easeOut"
      }
    }
  };

  return (
    <section id="projects" className="projects">
      <div className="container">
        <motion.div
          ref={ref}
          variants={containerVariants}
          initial="hidden"
          animate={inView ? "visible" : "hidden"}
        >
          <div className="section-header">
            <motion.h2 className="section-title" variants={itemVariants}>
              Featured Projects
            </motion.h2>
            <motion.p className="section-subtitle" variants={itemVariants}>
              Showcasing my best work in game development, VR/AR, and AI integration
            </motion.p>
          </div>

          <motion.div className="project-filters" variants={itemVariants}>
            {categories.map(category => (
              <button
                key={category.id}
                className={`filter-btn ${filter === category.id ? 'active' : ''}`}
                onClick={() => setFilter(category.id)}
              >
                {category.label}
              </button>
            ))}
          </motion.div>

          <motion.div 
            className="projects-grid"
            variants={containerVariants}
            layout
          >
            {filteredProjects.map((project, index) => (
              <motion.div
                key={project.id}
                className={`project-card ${project.featured ? 'featured' : ''}`}
                variants={itemVariants}
                layout
                whileHover={{ y: -10 }}
                transition={{ duration: 0.3 }}
              >
                <div className="project-image">
                  <img src={project.image} alt={project.title} />
                  <div className="project-overlay">
                    <div className="project-links">
                      {project.github && (
                        <a 
                          href={project.github} 
                          target="_blank" 
                          rel="noopener noreferrer"
                          className="project-link"
                          title="View Source Code"
                        >
                          <FiGithub size={20} />
                        </a>
                      )}
                      {project.demo && (
                        <a 
                          href={project.demo} 
                          target="_blank" 
                          rel="noopener noreferrer"
                          className="project-link"
                          title="Live Demo"
                        >
                          <FiExternalLink size={20} />
                        </a>
                      )}
                    </div>
                  </div>
                  {project.featured && (
                    <div className="featured-badge">
                      <FiStar size={16} />
                      Featured
                    </div>
                  )}
                  <div className={`status-badge ${project.status}`}>
                    {project.status === 'completed' ? 'Completed' : 'In Progress'}
                  </div>
                </div>

                <div className="project-content">
                  <h3 className="project-title">{project.title}</h3>
                  <p className="project-description">{project.description}</p>
                  
                  <div className="project-tech">
                    {project.technologies.map((tech, techIndex) => (
                      <span key={techIndex} className="tech-badge">
                        {tech}
                      </span>
                    ))}
                  </div>

                  <div className="project-actions">
                    {project.github && (
                      <a 
                        href={project.github} 
                        target="_blank" 
                        rel="noopener noreferrer"
                        className="btn btn-outline btn-sm"
                      >
                        <FiCode size={16} />
                        Source Code
                      </a>
                    )}
                    {project.demo && (
                      <a 
                        href={project.demo} 
                        target="_blank" 
                        rel="noopener noreferrer"
                        className="btn btn-primary btn-sm"
                      >
                        <FiPlay size={16} />
                        Live Demo
                      </a>
                    )}
                  </div>
                </div>
              </motion.div>
            ))}
          </motion.div>

          <motion.div className="projects-cta" variants={itemVariants}>
            <h3>Interested in collaborating?</h3>
            <p>I'm always open to discussing new projects and opportunities</p>
            <a href="#contact" className="btn btn-primary">
              Let's Work Together
            </a>
          </motion.div>
        </motion.div>
      </div>
    </section>
  );
};

export default Projects;
