import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { FiDownload, FiMail, FiArrowDown } from 'react-icons/fi';
import './Hero.css';

const Hero = () => {
  const [displayText, setDisplayText] = useState('');
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);

  const roles = [
    'Software Engineer',
    'Unity Developer',
    'VR/AR Developer',
    'Game Developer',
    '.NET Core Developer',
    'AI Enthusiast'
  ];

  useEffect(() => {
    const typeSpeed = isDeleting ? 100 : 150;
    const currentRole = roles[currentIndex];

    const timer = setTimeout(() => {
      if (!isDeleting) {
        setDisplayText(currentRole.substring(0, displayText.length + 1));
        
        if (displayText === currentRole) {
          setTimeout(() => setIsDeleting(true), 2000);
        }
      } else {
        setDisplayText(currentRole.substring(0, displayText.length - 1));
        
        if (displayText === '') {
          setIsDeleting(false);
          setCurrentIndex((prevIndex) => (prevIndex + 1) % roles.length);
        }
      }
    }, typeSpeed);

    return () => clearTimeout(timer);
  }, [displayText, currentIndex, isDeleting, roles]);

  const scrollToContact = () => {
    const element = document.querySelector('#contact');
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        delayChildren: 0.3,
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { y: 50, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        duration: 0.8,
        ease: "easeOut"
      }
    }
  };

  return (
    <section id="hero" className="hero">
      <div className="hero-background">
        <div className="hero-grid"></div>
        <div className="hero-dots"></div>
      </div>
      
      <div className="container">
        <motion.div 
          className="hero-content"
          variants={containerVariants}
          initial="hidden"
          animate="visible"
        >
          <motion.div className="hero-text" variants={itemVariants}>
            <motion.p 
              className="hero-greeting"
              initial={{ opacity: 0, x: -30 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.6, delay: 0.2 }}
            >
              Hi there! 👋 I'm
            </motion.p>
            
            <motion.h1 
              className="hero-name"
              variants={itemVariants}
            >
              Abdullah <span className="hero-name-accent">Qadeer</span>
            </motion.h1>
            
            <motion.div 
              className="hero-role-container"
              variants={itemVariants}
            >
              <h2 className="hero-role">
                I'm a{' '}
                <span className="hero-role-dynamic">
                  {displayText}
                  <span className="cursor">|</span>
                </span>
              </h2>
            </motion.div>
            
            <motion.p 
              className="hero-description"
              variants={itemVariants}
            >
              Passionate about creating immersive experiences with Unity, VR/AR technologies, 
              and cutting-edge AI solutions. I specialize in building innovative applications 
              that bridge the gap between reality and digital experiences.
            </motion.p>
            
            <motion.div 
              className="hero-stats"
              variants={itemVariants}
            >
              <div className="stat">
                <span className="stat-number">5+</span>
                <span className="stat-label">Years Experience</span>
              </div>
              <div className="stat">
                <span className="stat-number">20+</span>
                <span className="stat-label">Projects Completed</span>
              </div>
              <div className="stat">
                <span className="stat-number">10+</span>
                <span className="stat-label">Technologies</span>
              </div>
            </motion.div>
          </motion.div>
          
          <motion.div 
            className="hero-actions"
            variants={itemVariants}
          >
            <motion.button 
              className="btn btn-primary"
              onClick={scrollToContact}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <FiMail size={18} />
              Get In Touch
            </motion.button>
            
            <motion.a 
              href="/Abdullah Qadeer - Software Engineer.pdf"
              download
              className="btn btn-secondary"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <FiDownload size={18} />
              Download CV
            </motion.a>
          </motion.div>
          
          <motion.div 
            className="hero-scroll-indicator"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 1.5 }}
          >
            <motion.div 
              className="scroll-arrow"
              animate={{ y: [0, 10, 0] }}
              transition={{ duration: 2, repeat: Infinity }}
            >
              <FiArrowDown size={20} />
            </motion.div>
            <span>Scroll to explore</span>
          </motion.div>
        </motion.div>
        
        <motion.div 
          className="hero-visual"
          initial={{ opacity: 0, scale: 0.8, x: 100 }}
          animate={{ opacity: 1, scale: 1, x: 0 }}
          transition={{ duration: 1, delay: 0.5 }}
        >
          <div className="hero-avatar">
            <div className="avatar-container">
              <div className="avatar-bg"></div>
              <div className="avatar-icon">
                <span>AQ</span>
              </div>
            </div>
            
            <div className="tech-icons">
              <motion.div 
                className="tech-icon unity"
                animate={{ rotate: 360 }}
                transition={{ duration: 20, repeat: Infinity, ease: "linear" }}
              >
                Unity
              </motion.div>
              <motion.div 
                className="tech-icon react"
                animate={{ y: [-10, 10, -10] }}
                transition={{ duration: 3, repeat: Infinity }}
              >
                React
              </motion.div>
              <motion.div 
                className="tech-icon csharp"
                animate={{ x: [-10, 10, -10] }}
                transition={{ duration: 4, repeat: Infinity }}
              >
                C#
              </motion.div>
              <motion.div 
                className="tech-icon vr"
                animate={{ rotate: [-15, 15, -15] }}
                transition={{ duration: 5, repeat: Infinity }}
              >
                VR
              </motion.div>
            </div>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default Hero;
