import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { FiCode, FiPlay, FiEye, FiCpu, FiAward, FiUsers } from 'react-icons/fi';
import './About.css';

const About = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { y: 50, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        duration: 0.6,
        ease: "easeOut"
      }
    }
  };

  const specialties = [
    {
      icon: FiGamepad2,
      title: "Game Development",
      description: "Expert in Unity 3D with 5+ years of experience creating immersive gaming experiences and interactive applications."
    },
    {
      icon: FiEye,
      title: "VR/AR Development", 
      description: "Specialized in virtual and augmented reality solutions using Unity XR, Oculus SDK, and cutting-edge immersive technologies."
    },
    {
      icon: FiCode,
      title: ".NET Core Development",
      description: "Proficient in building robust backend systems, APIs, and enterprise applications using .NET Core and C#."
    },
    {
      icon: FiCpu,
      title: "AI Integration",
      description: "Experienced in integrating AI and machine learning solutions into applications to enhance user experiences."
    }
  ];

  const achievements = [
    {
      icon: FiAward,
      number: "5+",
      label: "Years Experience"
    },
    {
      icon: FiCode,
      number: "20+",
      label: "Projects Delivered"
    },
    {
      icon: FiUsers,
      number: "10+",
      label: "Happy Clients"
    }
  ];

  return (
    <section id="about" className="about">
      <div className="container">
        <motion.div
          ref={ref}
          variants={containerVariants}
          initial="hidden"
          animate={inView ? "visible" : "hidden"}
        >
          <div className="section-header">
            <motion.h2 className="section-title" variants={itemVariants}>
              About Me
            </motion.h2>
            <motion.p className="section-subtitle" variants={itemVariants}>
              Passionate Software Engineer with expertise in Unity, VR/AR, and modern web technologies
            </motion.p>
          </div>

          <div className="about-content">
            <motion.div className="about-text" variants={itemVariants}>
              <div className="about-intro">
                <h3>Hi, I'm Abdullah Qadeer</h3>
                <p>
                  A dedicated Software Engineer with over 5 years of experience in game development, 
                  VR/AR technologies, and full-stack development. I'm passionate about creating 
                  innovative digital experiences that push the boundaries of what's possible.
                </p>
                <p>
                  My journey began with a fascination for interactive technologies, leading me to 
                  specialize in Unity development, virtual reality, and AI-driven solutions. 
                  I've worked with diverse teams to deliver cutting-edge applications ranging from 
                  immersive games to enterprise VR solutions.
                </p>
                <p>
                  When I'm not coding, you'll find me exploring the latest tech trends, contributing 
                  to open-source projects, or mentoring aspiring developers in the community.
                </p>
              </div>

              <div className="about-achievements">
                {achievements.map((achievement, index) => {
                  const Icon = achievement.icon;
                  return (
                    <motion.div
                      key={achievement.label}
                      className="achievement"
                      variants={itemVariants}
                      whileHover={{ scale: 1.05 }}
                    >
                      <div className="achievement-icon">
                        <Icon size={24} />
                      </div>
                      <div className="achievement-content">
                        <span className="achievement-number">{achievement.number}</span>
                        <span className="achievement-label">{achievement.label}</span>
                      </div>
                    </motion.div>
                  );
                })}
              </div>
            </motion.div>

            <motion.div className="about-specialties" variants={itemVariants}>
              <h3>What I Do</h3>
              <div className="specialties-grid">
                {specialties.map((specialty, index) => {
                  const Icon = specialty.icon;
                  return (
                    <motion.div
                      key={specialty.title}
                      className="specialty-card"
                      variants={itemVariants}
                      whileHover={{ y: -10 }}
                      transition={{ duration: 0.3 }}
                    >
                      <div className="specialty-icon">
                        <Icon size={32} />
                      </div>
                      <h4>{specialty.title}</h4>
                      <p>{specialty.description}</p>
                    </motion.div>
                  );
                })}
              </div>
            </motion.div>
          </div>

          <motion.div className="about-values" variants={itemVariants}>
            <h3>My Core Values</h3>
            <div className="values-grid">
              <div className="value-item">
                <h4>Innovation</h4>
                <p>Constantly exploring new technologies and pushing creative boundaries</p>
              </div>
              <div className="value-item">
                <h4>Quality</h4>
                <p>Committed to delivering exceptional code and user experiences</p>
              </div>
              <div className="value-item">
                <h4>Collaboration</h4>
                <p>Believing in the power of teamwork and knowledge sharing</p>
              </div>
              <div className="value-item">
                <h4>Growth</h4>
                <p>Embracing continuous learning and professional development</p>
              </div>
            </div>
          </motion.div>
        </motion.div>
      </div>
    </section>
  );
};

export default About;
