import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { 
  FiMail, 
  FiPhone, 
  FiMapPin, 
  FiLinkedin, 
  FiGithub, 
  FiExternalLink,
  FiSend,
  FiUser,
  FiMessageSquare,
  FiCheck,
  FiX
} from 'react-icons/fi';
import './Contact.css';

const Contact = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: ''
  });

  const [formStatus, setFormStatus] = useState({
    isSubmitting: false,
    isSubmitted: false,
    error: null
  });

  const [errors, setErrors] = useState({});

  const contactInfo = [
    {
      icon: FiMail,
      label: 'Email',
      value: 'abdullah.qadeer@example.com',
      href: 'mailto:abdullah.qadeer@example.com',
      description: 'Send me an email'
    },
    {
      icon: FiPhone,
      label: 'Phone',
      value: '+92 XXX XXXXXXX',
      href: 'tel:+92XXXXXXXXX',
      description: 'Give me a call'
    },
    {
      icon: FiMapPin,
      label: 'Location',
      value: 'Remote / Pakistan',
      href: '#',
      description: 'Based in Pakistan, working remotely'
    }
  ];

  const socialLinks = [
    {
      name: 'LinkedIn',
      icon: FiLinkedin,
      url: 'https://www.linkedin.com/in/abdullahqadeerdev',
      color: '#0077B5',
      description: 'Connect with me professionally'
    },
    {
      name: 'GitHub',
      icon: FiGithub,
      url: 'https://github.com/abdullah6930',
      color: '#333',
      description: 'Check out my code'
    },
    {
      name: 'Portfolio',
      icon: FiExternalLink,
      url: 'https://abdullah-qadeer-portfolio.web.app/',
      color: '#0070f3',
      description: 'View my portfolio'
    }
  ];

  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Name is required';
    }

    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (!formData.subject.trim()) {
      newErrors.subject = 'Subject is required';
    }

    if (!formData.message.trim()) {
      newErrors.message = 'Message is required';
    } else if (formData.message.trim().length < 10) {
      newErrors.message = 'Message must be at least 10 characters long';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setFormStatus({
      isSubmitting: true,
      isSubmitted: false,
      error: null
    });

    try {
      // Here you would integrate with EmailJS or your backend
      // For now, we'll simulate an API call
      await new Promise(resolve => setTimeout(resolve, 2000));

      setFormStatus({
        isSubmitting: false,
        isSubmitted: true,
        error: null
      });

      // Reset form
      setFormData({
        name: '',
        email: '',
        subject: '',
        message: ''
      });

      // Reset success message after 5 seconds
      setTimeout(() => {
        setFormStatus(prev => ({
          ...prev,
          isSubmitted: false
        }));
      }, 5000);

    } catch (error) {
      setFormStatus({
        isSubmitting: false,
        isSubmitted: false,
        error: 'Failed to send message. Please try again.'
      });
    }
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { y: 30, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        duration: 0.5
      }
    }
  };

  return (
    <section id="contact" className="contact">
      <div className="container">
        <motion.div
          ref={ref}
          variants={containerVariants}
          initial="hidden"
          animate={inView ? "visible" : "hidden"}
        >
          <div className="section-header">
            <motion.h2 
              variants={itemVariants}
              className="section-title"
            >
              Get In Touch
            </motion.h2>
            <motion.p 
              variants={itemVariants}
              className="section-subtitle"
            >
              I'm always interested in new opportunities and exciting projects. Let's connect!
            </motion.p>
          </div>

          <div className="contact-content">
            <motion.div 
              variants={itemVariants}
              className="contact-form-container"
            >
              <div className="form-header">
                <h3>Send Me a Message</h3>
                <p>Fill out the form below and I'll get back to you as soon as possible.</p>
              </div>

              {formStatus.isSubmitted && (
                <motion.div 
                  className="success-message"
                  initial={{ opacity: 0, scale: 0.8 }}
                  animate={{ opacity: 1, scale: 1 }}
                  transition={{ duration: 0.3 }}
                >
                  <FiCheck />
                  <span>Message sent successfully! I'll get back to you soon.</span>
                </motion.div>
              )}

              {formStatus.error && (
                <motion.div 
                  className="error-message"
                  initial={{ opacity: 0, scale: 0.8 }}
                  animate={{ opacity: 1, scale: 1 }}
                  transition={{ duration: 0.3 }}
                >
                  <FiX />
                  <span>{formStatus.error}</span>
                </motion.div>
              )}

              <form onSubmit={handleSubmit} className="contact-form">
                <div className="form-row">
                  <div className="form-group">
                    <label htmlFor="name">
                      <FiUser />
                      Name *
                    </label>
                    <input
                      type="text"
                      id="name"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                      className={errors.name ? 'error' : ''}
                      placeholder="Your name"
                    />
                    {errors.name && <span className="field-error">{errors.name}</span>}
                  </div>

                  <div className="form-group">
                    <label htmlFor="email">
                      <FiMail />
                      Email *
                    </label>
                    <input
                      type="email"
                      id="email"
                      name="email"
                      value={formData.email}
                      onChange={handleInputChange}
                      className={errors.email ? 'error' : ''}
                      placeholder="your.email@example.com"
                    />
                    {errors.email && <span className="field-error">{errors.email}</span>}
                  </div>
                </div>

                <div className="form-group">
                  <label htmlFor="subject">
                    <FiMessageSquare />
                    Subject *
                  </label>
                  <input
                    type="text"
                    id="subject"
                    name="subject"
                    value={formData.subject}
                    onChange={handleInputChange}
                    className={errors.subject ? 'error' : ''}
                    placeholder="What's this about?"
                  />
                  {errors.subject && <span className="field-error">{errors.subject}</span>}
                </div>

                <div className="form-group">
                  <label htmlFor="message">
                    <FiMessageSquare />
                    Message *
                  </label>
                  <textarea
                    id="message"
                    name="message"
                    value={formData.message}
                    onChange={handleInputChange}
                    className={errors.message ? 'error' : ''}
                    placeholder="Tell me about your project or idea..."
                    rows="6"
                  />
                  {errors.message && <span className="field-error">{errors.message}</span>}
                </div>

                <motion.button
                  type="submit"
                  className="submit-btn"
                  disabled={formStatus.isSubmitting}
                  whileHover={{ scale: formStatus.isSubmitting ? 1 : 1.02 }}
                  whileTap={{ scale: formStatus.isSubmitting ? 1 : 0.98 }}
                >
                  {formStatus.isSubmitting ? (
                    <>
                      <div className="spinner" />
                      Sending...
                    </>
                  ) : (
                    <>
                      <FiSend />
                      Send Message
                    </>
                  )}
                </motion.button>
              </form>
            </motion.div>

            <motion.div 
              variants={itemVariants}
              className="contact-info"
            >
              <div className="info-header">
                <h3>Contact Information</h3>
                <p>Feel free to reach out through any of these channels.</p>
              </div>

              <div className="contact-methods">
                {contactInfo.map((info, index) => (
                  <motion.a
                    key={index}
                    href={info.href}
                    className="contact-method"
                    whileHover={{ x: 5 }}
                    transition={{ duration: 0.2 }}
                  >
                    <div className="method-icon">
                      <info.icon />
                    </div>
                    <div className="method-content">
                      <h4>{info.label}</h4>
                      <p>{info.value}</p>
                      <span>{info.description}</span>
                    </div>
                  </motion.a>
                ))}
              </div>

              <div className="social-section">
                <h4>Follow Me</h4>
                <div className="social-links">
                  {socialLinks.map((social, index) => (
                    <motion.a
                      key={index}
                      href={social.url}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="social-link"
                      style={{ borderColor: social.color }}
                      whileHover={{ 
                        scale: 1.1,
                        backgroundColor: social.color,
                        color: 'white'
                      }}
                      transition={{ duration: 0.2 }}
                    >
                      <social.icon />
                      <span className="social-tooltip">{social.description}</span>
                    </motion.a>
                  ))}
                </div>
              </div>

              <div className="response-time">
                <h4>Response Time</h4>
                <p>I typically respond within 24 hours. For urgent matters, feel free to call.</p>
              </div>
            </motion.div>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default Contact;
