import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { 
  FiCode, 
  FiDatabase, 
  FiCloud, 
  FiSmartphone, 
  FiPlay, 
  FiEye, 
  FiCpu, 
  FiTool,
  FiGitBranch,
  FiLayers
} from 'react-icons/fi';
import './Skills.css';

const Skills = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const skillCategories = [
    {
      id: 1,
      title: "Game Development",
      icon: FiPlay,
      color: "#FF6B6B",
      skills: [
        { name: "Unity 3D", level: 95, description: "5+ years of experience" },
        { name: "C#", level: 90, description: "Primary programming language" },
        { name: "Game Design", level: 85, description: "Mechanics & Systems" },
        { name: "2D/3D Animation", level: 80, description: "Character & Environment" }
      ]
    },
    {
      id: 2,
      title: "VR/AR Development",
      icon: FiEye,
      color: "#4ECDC4",
      skills: [
        { name: "Oculus SDK", level: 90, description: "Quest & Rift development" },
        { name: "OpenXR", level: 85, description: "Cross-platform VR" },
        { name: "ARCore/ARKit", level: 80, description: "Mobile AR solutions" },
        { name: "Spatial Computing", level: 85, description: "3D interaction design" }
      ]
    },
    {
      id: 3,
      title: "Backend Development",
      icon: FiDatabase,
      color: "#45B7D1",
      skills: [
        { name: ".NET Core", level: 90, description: "RESTful APIs & Microservices" },
        { name: "SQL Server", level: 85, description: "Database design & optimization" },
        { name: "Entity Framework", level: 85, description: "ORM & Code First" },
        { name: "Azure Services", level: 80, description: "Cloud deployment & scaling" }
      ]
    },
    {
      id: 4,
      title: "Frontend Development",
      icon: FiCode,
      color: "#F7DC6F",
      skills: [
        { name: "React.js", level: 85, description: "Modern web applications" },
        { name: "JavaScript", level: 85, description: "ES6+ & TypeScript" },
        { name: "HTML/CSS", level: 90, description: "Responsive design" },
        { name: "Redux", level: 75, description: "State management" }
      ]
    },
    {
      id: 5,
      title: "AI & Machine Learning",
      icon: FiCpu,
      color: "#BB8FCE",
      skills: [
        { name: "TensorFlow", level: 75, description: "Deep learning models" },
        { name: "OpenAI API", level: 85, description: "GPT integration" },
        { name: "Computer Vision", level: 70, description: "Image recognition" },
        { name: "Natural Language Processing", level: 75, description: "Text analysis" }
      ]
    },
    {
      id: 6,
      title: "DevOps & Tools",
      icon: FiTool,
      color: "#58D68D",
      skills: [
        { name: "Git", level: 90, description: "Version control & collaboration" },
        { name: "Docker", level: 75, description: "Containerization" },
        { name: "Azure DevOps", level: 80, description: "CI/CD pipelines" },
        { name: "Visual Studio", level: 95, description: "IDE mastery" }
      ]
    }
  ];

  const certifications = [
    {
      name: "Unity Certified Programmer",
      issuer: "Unity Technologies",
      year: "2023",
      status: "Active"
    },
    {
      name: "Microsoft Azure Fundamentals",
      issuer: "Microsoft",
      year: "2022",
      status: "Active"
    },
    {
      name: "C# Programming Certification",
      issuer: "Microsoft",
      year: "2021",
      status: "Active"
    }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { y: 30, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        duration: 0.5
      }
    }
  };

  const skillBarVariants = {
    hidden: { width: 0 },
    visible: (level) => ({
      width: `${level}%`,
      transition: {
        duration: 1,
        delay: 0.2,
        ease: "easeOut"
      }
    })
  };

  return (
    <section id="skills" className="skills">
      <div className="container">
        <motion.div
          ref={ref}
          variants={containerVariants}
          initial="hidden"
          animate={inView ? "visible" : "hidden"}
        >
          <div className="section-header">
            <motion.h2 
              variants={itemVariants}
              className="section-title"
            >
              Skills & Expertise
            </motion.h2>
            <motion.p 
              variants={itemVariants}
              className="section-subtitle"
            >
              Comprehensive technical skills across game development, VR/AR, web technologies, and AI
            </motion.p>
          </div>

          <motion.div 
            variants={itemVariants}
            className="skills-grid"
          >
            {skillCategories.map((category) => (
              <motion.div 
                key={category.id} 
                className="skill-category"
                whileHover={{ y: -5 }}
                transition={{ duration: 0.2 }}
              >
                <div className="category-header">
                  <div 
                    className="category-icon"
                    style={{ color: category.color }}
                  >
                    <category.icon size={24} />
                  </div>
                  <h3 className="category-title">{category.title}</h3>
                </div>
                
                <div className="skills-list">
                  {category.skills.map((skill, index) => (
                    <div key={index} className="skill-item">
                      <div className="skill-info">
                        <span className="skill-name">{skill.name}</span>
                        <span className="skill-level">{skill.level}%</span>
                      </div>
                      <div className="skill-bar">
                        <motion.div 
                          className="skill-progress"
                          style={{ backgroundColor: category.color }}
                          variants={skillBarVariants}
                          custom={skill.level}
                          initial="hidden"
                          animate={inView ? "visible" : "hidden"}
                        />
                      </div>
                      <p className="skill-description">{skill.description}</p>
                    </div>
                  ))}
                </div>
              </motion.div>
            ))}
          </motion.div>

          <motion.div 
            variants={itemVariants}
            className="certifications-section"
          >
            <h3 className="certifications-title">Certifications & Achievements</h3>
            <div className="certifications-grid">
              {certifications.map((cert, index) => (
                <motion.div 
                  key={index} 
                  className="certification-item"
                  whileHover={{ scale: 1.02 }}
                  transition={{ duration: 0.2 }}
                >
                  <div className="cert-icon">
                    <FiLayers />
                  </div>
                  <div className="cert-content">
                    <h4 className="cert-name">{cert.name}</h4>
                    <p className="cert-issuer">{cert.issuer}</p>
                    <div className="cert-meta">
                      <span className="cert-year">{cert.year}</span>
                      <span className={`cert-status ${cert.status.toLowerCase()}`}>
                        {cert.status}
                      </span>
                    </div>
                  </div>
                </motion.div>
              ))}
            </div>
          </motion.div>

          <motion.div 
            variants={itemVariants}
            className="skills-summary"
          >
            <div className="summary-stats">
              <div className="stat-item">
                <div className="stat-number">5+</div>
                <div className="stat-label">Years Experience</div>
              </div>
              <div className="stat-item">
                <div className="stat-number">20+</div>
                <div className="stat-label">Technologies</div>
              </div>
              <div className="stat-item">
                <div className="stat-number">15+</div>
                <div className="stat-label">Projects Completed</div>
              </div>
              <div className="stat-item">
                <div className="stat-number">3</div>
                <div className="stat-label">Certifications</div>
              </div>
            </div>
          </motion.div>
        </motion.div>
      </div>
    </section>
  );
};

export default Skills;
